module paint_application(
input CLOCK, input [11:0]xpos, input [11:0]ypos, input middle, input left, input right, input [2:0]sw, output reg [15:13]led, 
output [7:0]JC
);
    
    wire frame_begin;
    wire [12:0] pixel_index;
    wire sending_pixels;
    wire sample_pixel;
    wire clock_LED;
    wire [8:0] x,y;
    reg [15:0]oled_data= 16'b1111111111111111;
    reg middle_pressed = 0;
    reg [11:0]ypos_adjusted;
    reg [11:0]xpos_adjusted;

    //For Drawing Canvas
    reg [15:0]pixel_status[97:4478];
    reg [31:0]counter;
    
    //For Mouse Toggle
    reg [31:0]click_time_counter = 0;
    reg click_toggle = 0;
    
    //For recognition of colour clicked on by left and right mouse button.
    //0 = White, 1 = Black, 2 = Red , 3 = Pink, 4 = Yellow, 5 = Green, 6 = Blue, 7 = Purple
    reg [2:0]left_colour_selected = 1;
    reg [2:0]right_colour_selected = 1;
    
    //Thickness of Pen
    reg [2:0]pen_thickness = 0;
    reg [15:0]left_pen_colour = 0;
    reg [15:0]right_pen_colour = 0;
    reg [15:0]fillmode_pen = 0;
    
    //For Switching Between Draw and Fill Mode
    reg [15:0]red = 16'b1111100000000000;
    reg [15:0]green = 16'b0000011111100000;
    reg fill_toggle = 0;
    reg draw_active = 1;
    reg expand_active = 0;
    
//    //For Switch Between Swap and Replace Colour Mode
//    reg swap_active = 0;
//    reg replace_active = 0;
//    reg left_exist = 0;
//    reg right_exist = 0;
    
    always @ (posedge CLOCK) begin         
        case (pen_thickness)
        0: begin
        ypos_adjusted[11:0] <= (ypos <= 0) ? 0 : 
                               (ypos >= 63) ? 63 : ypos;
//        xpos_adjusted[11:0] <= (xpos <= 0) ? 0 :
//                               (xpos >= 95) ? 95 : xpos;
        xpos_adjusted[11:0] <= (xpos >= 95) ? (xpos <= 96) ? 95 : 0 : xpos;
        end
        1: begin
        ypos_adjusted[11:0] <= (ypos <= 1) ? 1 : 
                               (ypos >= 62) ? 62 : ypos;
//        
        xpos_adjusted[11:0] <= (xpos >= 94) ? (xpos <= 96) ? 94 : 1 : xpos;
        end
        2: begin
        ypos_adjusted[11:0] <= (ypos <= 2) ? 2 : 
                               (ypos >= 61) ? 61 : ypos;
//        xpos_adjusted[11:0] <= (xpos <= 2) ? 2 :
//                               (xpos >= 93) ? 93 : xpos;
        xpos_adjusted[11:0] <= (xpos >= 93) ? (xpos <= 96) ? 94 : 2 : xpos;
        end                       
        endcase                       
        led[13] <= (right == 1) ? 1 : 0;
        led[14] <= (middle == 1) ? 1 : 0;
        led[15] <= (left == 1) ? 1 : 0;
    end
    
    Pixel_toXY pixel_XY(pixel_index[12:0], x, y);
    
    my_clk_divider LED_Clock(CLOCK, clock_LED, 7);
    
initial begin
    for (counter = 0; counter <= 4478; counter = counter + 1) begin //97
        pixel_status[counter] = 16'b1111111111111111;
    end
end

always @(posedge clock_LED) begin
    //Mouse Click Toggle (To Prevent Accidental Clicking when Holding mouse button down.)
    if (left == 1 || right == 1) begin
        click_time_counter <= (click_time_counter == 200000) ? 200000 : click_time_counter + 1;
        if (click_time_counter <= 1000) begin
            fill_toggle <= 1;
        end
        else begin
            fill_toggle <= 0;
        end
        if (click_time_counter <= 199999) begin
            click_toggle <= 1;
        end
        else if (click_time_counter > 199999) begin
            click_toggle <= 0;
        end
    end
    else begin
        click_toggle <= 0;
        fill_toggle <= 0;
        click_time_counter <= 0;
    end
    
    //Adjust Thickness of Pen
    case (sw[2:1])
    2'b00: begin
        pen_thickness <= 0;
    end
    2'b01: begin
        pen_thickness <= 1;
    end
    2'b10, 2'b11: begin
        pen_thickness <= 2;
    end
